xwikidoc.content:
import java.io.Writer
import java.io.StringWriter
import com.opencsv.CSVWriter

class CsvWriter {
  def stringWriter = new StringWriter();

  def CSVWriter csvWriter

  CsvWriter() {
    super()
  }

  String debug() {
    return "Writer: $csvWriter"
  }

  void initialize() {
    this.csvWriter = new CSVWriter(stringWriter, CSVWriter.DEFAULT_SEPARATOR, CSVWriter.DEFAULT_QUOTE_CHARACTER, CSVWriter.DEFAULT_ESCAPE_CHARACTER, CSVWriter.DEFAULT_LINE_END)
  }

  void writeNext(List list) {
    def array = list.toArray(new String[0])
    csvWriter.writeNext(array, true)
  }

  void close() {
    if (csvWriter != null) {
      csvWriter.close()
    }
  }

  String getCSV() {
    return stringWriter.toString();
  }
}
